mapper.select:
SELECT seq, id, ref, step, depth, title, content, created_at createdAt, del, read_count readCount FROM (SELECT row_number() OVER(ORDER BY ref DESC, step ASC) AS rnum, seq, id, ref, step, depth, title, content, created_at, del, read_count FROM bbs
WHERE title LIKE concat('%', #{search}, '%')
WHERE content LIKE concat('%', #{search}, '%')
WHERE id=#{search}
ORDER BY ref DESC, step ASC) a WHERE rnum BETWEEN ${pageStart} AND ${pageEnd}

mapper.insert:
INSERT INTO read_history (bbs_seq, id, latest_access_at) VALUES (#{bbsSeq}, #{readerId}, NOW())
ON DUPLICATE KEY
UPDATE latest_access_at=NOW();

mapper.update:
UPDATE bbs
SET read_count = read_count + 1
WHERE seq = #{seq}

		UPDATE bbs
		SET content=#{content}, title=#{title}
		WHERE seq=#{seq}

UPDATE bbs
SET del = 1
WHERE seq=#{seq}
